package 제어문.반복문;

public class 반복문연습1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			System.out.print("*");
		}
		System.out.println("");
		for (int i = 0; i < 5; i++) {
			System.out.print("커피*");
		}
		System.out.println("");
		for (int i = 0; i < 3; i++) {
			System.out.println("커피*우유");
		}
		for (int i = 1; i < 4; i++) {
			System.out.println(i + " : 짱!");
		}
	}

}
